from optparse import OptionParserusage = "usage:  %prog [option] arg1 arg2"parser = OptionParser(usage=usage)parser.add_option("-v", "--verbose",                                  action="store_true",dest="verbose",default="True",                                  help="Make lots of noise [default]")parser.add_option("-q", "--quiet",                                  action="store_false",dest="verbose",                                  help="be vewwy quiet (I'm hunting wabbits)")parser.add_option("-f", "--filename",                                  metavar="FILE",help="Write output to FILE")parser.add_option("-m", "--mode",                                  default="intermediate",                                  help="interaction mode: novice , intermediate, "                                  "or exper [default: %default]"                                 )(options, args) = parser.parse_args( )